import React, { useState, Fragment } from 'react';
import '../Styles/App.css';
import Navbar1 from './Layout/navbar';
import Users from './Users/Users';
import User from './Users/user';
import { BrowserRouter as MyRouter, Switch, Route } from 'react-router-dom';
import axios from 'axios';
import Search from './Users/search';
import Alert from './Layout/alert';
import About from './Pages/about';
import GithubState from '../context/github/GithubState';




const App = () => {

  const [repos, setRepos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState([]);

  // async componentDidMount() {

  //   this.setState({ loading: true });

  //   const res = await axios.get(`https://api.github.com/users?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`)
  //   console.log(res.data);

  //   this.setState({ users: res.data, loading: false })
  // }


  const showAlert = (msg, type) => { // setting an alert function to display if the input field was submitted empty
    setAlert({ msg, type });

    // setting a time limit for the displayed alert to disappear.
    setTimeout(() => setAlert(null), 5000);

  };



  //const { users, user, loading, repos } = this.state

  return (
    <GithubState>



      <MyRouter>

        <div className="App">
          <div>
            <Navbar1 />
          </div>

          <div className="container">
            <Alert alert={alert} />

            <Switch>

              <Route exact path='/' render={props => (
                <Fragment>
                  <Search
                    setAlert={showAlert}
                  />

                  <Users />


                </Fragment>
              )} />

              {/* second router page*/}
              <Route exact path='/about' component={About} /> {/*exact: is used to make sure that only the exact path is returned */}

              {/* third router page*/}
              <Route exact path='/user/:login' render={props => (
                <User {...props}
                  getUserRepos={getUserRepos}
                  repos={repos}
                  loading={loading} />
              )} />
            </Switch>
            {/* <About /> */}

          </div>


        </div>

      </MyRouter>
    </GithubState>

  );

}

export default App;
