import React, { Component, Fragment } from 'react';
import '../Styles/App.css';
import Navbar1 from './Layout/navbar';
import Users from './Users/Users';
import { BrowserRouter as MyRouter, Switch, Route } from 'react-router-dom';
import axios from 'axios';
import Search from './Users/search';
import Alert from './Layout/alert';
// import { library } from '@fortawesome/fontawesome-svg-core';
// import { faGitHub } from '@fortawesome/free-solid-svg-icons';

// library.add(faGitHub);

class App extends Component {

  state = {
    users: [],
    loading: false,
    alert: null,
  }
  // async componentDidMount() {

  //   this.setState({ loading: true });

  //   const res = await axios.get(`https://api.github.com/users?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`)
  //   console.log(res.data);

  //   this.setState({ users: res.data, loading: false })
  // }

  // to find Github users according to the text entered.
  searchUsers = async (text) => {

    this.setState({ loading: true });

    const res = await axios.get(`https://api.github.com/search/users?q=${text}&client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`)
    console.log(res.data.items);

    this.setState({ users: res.data.items, loading: false })
  }

  // to clear searched users from state
  clearUsers = () => {
    this.setState({
      user: [],
      loading: false,
    });
  };

  setAlert = (msg, type) => { // setting an alert function to display if the input field was submitted empty
    this.setState({
      alert: { msg, type }
    });

    // setting a time limit for the displayed alert to disappear.
    setTimeout(() => this.setState({ alert: null }), 5000)

  };

  render() {

    const { users, loading } = this.state

    return (
      <MyRouter>

        <div className="App">
          <div>
            <Navbar1 />
          </div>

          <div className="container">
            <Alert alert={this.state.alert} />

            <Switch>

              <Route exact path='/' render={props => (
                <Fragment>

                  <Search
                    searchUsers={this.searchUsers}
                    clearUsers={this.clearUsers}
                    showClear={users.length > 0 ? true : false}
                    setAlert={this.setAlert}
                  />

                  <Users
                    loading={loading}
                    users={users}
                  />

                </Fragment>
              )} />
            </Switch>

          </div>


        </div>

      </MyRouter>

    );
  }
}

export default App;
